/**
 * TABS
 */
$(document).ready(function() {

    $( function() {
    $( "#tabs" ).tabs({
        collapsible: true,
        hide: { effect: "fade", duration: 300, easing: "easeInOutQuart" },
        show: { effect: "fade", duration: 300, easing: "easeInOutQuart" }
    });
    } );

});